#!/bin/sh
# This file is generated by catkin. 

#
# path segment PREFIX/lib/pythonX.Y/[dist|site]-packages in PYTHONPATH is calculated
# by us... this appears to be what setuptools does under --install-layout=deb
#

PYTHONPATH=/usr/local/lib/python2.7/dist-packages:$PYTHONPATH
export PYTHONPATH

PATH=/usr/local/bin:$PATH
export PATH

#the first  path: to have custom cmake modules inside Modules (e.g. Eigen)
#the second path: this is so that find_package works well...
CMAKE_PREFIX_PATH=/usr/local/share/catkin/cmake/Modules:/usr/local:$CMAKE_PREFIX_PATH
export CMAKE_PREFIX_PATH

UNAME=`which uname`
if [ `$UNAME` = Darwin ]; then
  DYLD_LIBRARY_PATH=/usr/local/lib:$DYLD_LIBRARY_PATH
  export DYLD_LIBRARY_PATH
else
  LD_LIBRARY_PATH=/usr/local/lib:$LD_LIBRARY_PATH
  export LD_LIBRARY_PATH
fi

PKG_CONFIG_PATH=/usr/local/lib/pkgconfig:$PKG_CONFIG_PATH
export PKG_CONFIG_PATH

if [ -z "$CATKIN_SHELL" ] ; then
   CATKIN_SHELL=sh
fi

FIND=`which find`
SORT=`which sort`
for envfile in `$FIND /usr/local/etc/catkin/profile.d -maxdepth 1 -name "*.all" 2>/dev/null | $SORT` `$FIND /usr/local/etc/catkin/profile.d -maxdepth 1 -name "*.$CATKIN_SHELL" 2>/dev/null | $SORT`
do
   . $envfile
done

